%== Bankrupt ================================================================

bankrupt :-

    turn(Player),
    location(Player, CurLoc), propOwner(CurLoc, Owner),
    
    retractall(winner(_)), asserta(winner(Owner)),
    retractall(isOngoing(_)), asserta(isOngoing(0)),
    
    write(''), nl,
    write('=============================================================='), nl,
    write('\tBANGKRUT!'),nl,
    write('--------------------------------------------------------------'), nl,
    write('\t'), nl,
    write('\tOh tidak! Anda bangkrut :('),nl,
    write(''), nl,
    write('=============================================================='), nl,
    write(''), nl,
    
    winScreen(Owner),
    
    !.

